all_data <- sqldf("select SGD.sgd_id, SGD.sys_name,in_degree.in_degree,dn_ds_sgd.ds, dn_ds_sgd.dn, dn_ds_sgd.dn_ds, dn_ds_sgd.dn_ds_adj, protein_hl_sgd.hl_min as protein_halflife,mRNA_abun_sgd.mRNA_abun_array, mRNA_abun_sgd.mRNA_abun_seq, mRNA_abun_sgd.mRNA_abun_sum as mRNA_abun_mean, pars_sgd.mean as pars, protein_abun_sgd.abundance as protein_abundance, mRNA_hl_sgd.mRNA_halflife, mf_all_seq_sgd.mf as all_seq_mf, mf_cds_sgd.cds_mf as cds_mf,mf_utr5_sgd.utr5_mf, mf_utr3_sgd.utr3_mf\nfrom SGD left join pars_sgd on SGD.sgd_id = pars_sgd.sgd_id\n                  left join protein_abun_sgd on protein_abun_sgd.sgd_id = SGD.sgd_id \n                  left join mf_all_seq_sgd on mf_all_seq_sgd.sgd_id = SGD.sgd_id\n                  left join mf_cds_sgd on mf_cds_sgd.sgd_id = SGD.sgd_id \n                  left join mf_utr5_sgd on mf_utr5_sgd.sgd_id = SGD.sgd_id\n                  left join mf_utr3_sgd on mf_utr3_sgd.sgd_id = SGD.sgd_id   left join mRNA_hl_sgd on mRNA_hl_sgd.sgd_id = SGD.sgd_id   left join dn_ds_sgd on dn_ds_sgd.sgd_id = SGD.sgd_id   left join in_degree on in_degree.name = SGD.sgd_id   left join protein_hl_sgd on protein_hl_sgd.sgd_id = SGD.sgd_id  left join mRNA_abun_sgd on mRNA_abun_sgd.sgd_id = SGD.sgd_id")